{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"},"kaggle":{"accelerator":"none","dataSources":[{"sourceId":91722,"databundleVersionId":14262372,"isSourceIdPinned":false,"sourceType":"competition"}],"dockerImageVersionId":31153,"isInternetEnabled":false,"language":"python","sourceType":"notebook","isGpuEnabled":false}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# filename: loanpredict.py\n# Kaggle S5E11 â€” Loan Payback\n# High-signal CatBoost-only pipeline with fold-wise target encodings and calibration.\n\nimport os\nimport warnings\nfrom typing import List, Tuple\nimport numpy as np\nimport pandas as pd\n\nfrom sklearn.model_selection import StratifiedKFold\nfrom sklearn.metrics import roc_auc_score\nfrom sklearn.isotonic import IsotonicRegression\n\nfrom catboost import CatBoostClassifier, Pool\n\nwarnings.filterwarnings(\"ignore\")\nSEED = 42\nN_FOLDS = 7  # stabilize variance vs 5-fold\n\n# ------------------------------\n# Data\n# ------------------------------\ndef load_data(target_col=\"loan_paid_back\", id_col=\"id\"):\n    train_path = \"train.csv\" if os.path.exists(\"train.csv\") else \"/kaggle/input/playground-series-s5e11/train.csv\"\n    test_path  = \"test.csv\"  if os.path.exists(\"test.csv\")  else \"/kaggle/input/playground-series-s5e11/test.csv\"\n    train, test = pd.read_csv(train_path), pd.read_csv(test_path)\n    y = train[target_col].astype(float)\n    X = train.drop(columns=[target_col])\n    test_ids = test[id_col].copy()\n    return X, y, test, test_ids\n\n# ------------------------------\n# Features\n# ------------------------------\ndef engineer(df: pd.DataFrame) -> pd.DataFrame:\n    out = df.copy()\n    for c in [\"annual_income\",\"debt_to_income_ratio\",\"credit_score\",\"loan_amount\",\"interest_rate\"]:\n        if c in out.columns: out[c] = pd.to_numeric(out[c], errors=\"coerce\")\n\n    if {\"loan_amount\",\"annual_income\"}.issubset(out.columns):\n        out[\"loan_to_income\"] = out[\"loan_amount\"]/(out[\"annual_income\"]+1.0)\n        out[\"log_loan_to_income\"] = np.log1p(out[\"loan_to_income\"].clip(lower=0))\n\n    if {\"interest_rate\",\"debt_to_income_ratio\"}.issubset(out.columns):\n        out[\"interest_burden\"] = out[\"interest_rate\"]*out[\"debt_to_income_ratio\"]\n\n    if \"credit_score\" in out.columns:\n        cs = out[\"credit_score\"].astype(float)\n        out[\"credit_score_norm\"] = (cs - cs.min())/(cs.max() - cs.min() + 1e-9)\n\n    if \"grade_subgrade\" in out.columns:\n        g = out[\"grade_subgrade\"].astype(str)\n        out[\"grade_letter\"] = g.str[0]\n        letter_map = {\"A\":6,\"B\":5,\"C\":4,\"D\":3,\"E\":2,\"F\":1}\n        out[\"grade_letter_ord\"] = out[\"grade_letter\"].map(letter_map).fillna(3).astype(int)\n\n    if {\"loan_to_income\",\"interest_rate\"}.issubset(out.columns):\n        out[\"lti_x_int\"] = out[\"loan_to_income\"]*out[\"interest_rate\"]\n    if {\"debt_to_income_ratio\",\"loan_to_income\"}.issubset(out.columns):\n        out[\"dti_x_lti\"] = out[\"debt_to_income_ratio\"]*out[\"loan_to_income\"]\n    return out\n\ndef infer_cats(df: pd.DataFrame, max_card=64) -> List[str]:\n    cats=[]\n    for c in df.columns:\n        if df[c].dtype == \"object\":\n            cats.append(c)\n        elif pd.api.types.is_integer_dtype(df[c]) and df[c].nunique() <= max_card:\n            cats.append(c)\n    return cats\n\n# ------------------------------\n# Strict fold-wise target mean encoding\n# ------------------------------\ndef target_mean_encode_cv(\n    X: pd.DataFrame, y: pd.Series, T: pd.DataFrame, cols: List[str], n_splits: int = N_FOLDS, seed: int = SEED\n) -> Tuple[pd.DataFrame, pd.DataFrame]:\n    X_enc, T_enc = X.copy(), T.copy()\n    global_mean = y.mean()\n    skf = StratifiedKFold(n_splits=n_splits, shuffle=True, random_state=seed)\n    for col in cols:\n        oof = np.zeros(len(X))\n        full_map = y.groupby(X[col]).mean()\n        T_map = T[col].map(full_map).fillna(global_mean)\n        for tr_idx, val_idx in skf.split(X, y):\n            fold_map = y.iloc[tr_idx].groupby(X.iloc[tr_idx][col]).mean()\n            oof[val_idx] = X.iloc[val_idx][col].map(fold_map).fillna(global_mean).values\n        X_enc[f\"{col}_tgtmean\"] = oof\n        T_enc[f\"{col}_tgtmean\"] = T_map.values\n    return X_enc, T_enc\n\n# ------------------------------\n# CatBoost config\n# ------------------------------\nCAT_PARAMS = {\n    \"loss_function\":\"Logloss\",\n    \"eval_metric\":\"AUC\",\n    \"learning_rate\":0.033,\n    \"depth\":8,\n    \"l2_leaf_reg\":5.0,\n    \"iterations\":4000,\n    \"bootstrap_type\":\"Bayesian\",\n    \"random_seed\":SEED,\n    \"verbose\":False,\n    # Ordered boosting early stopping\n    \"od_type\":\"Iter\",\n    \"od_wait\":600,\n}\n\n# ------------------------------\n# Training\n# ------------------------------\ndef run():\n    X_raw, y, T_raw, test_ids = load_data()\n    X, T = engineer(X_raw), engineer(T_raw)\n    common = [c for c in X.columns if c in T.columns]\n    X, T = X[common].copy(), T[common].copy()\n\n    cat_cols = infer_cats(X)\n    for c in cat_cols:\n        X[c] = X[c].astype(\"object\").fillna(\"__MISSING__\")\n        T[c] = T[c].astype(\"object\").fillna(\"__MISSING__\")\n\n    high_signal = [c for c in [\"grade_subgrade\",\"loan_purpose\",\"employment_status\"] if c in cat_cols]\n    X_te, T_te = target_mean_encode_cv(X, y, T, high_signal, n_splits=N_FOLDS, seed=SEED)\n\n    # CatBoost gets raw categoricals + target-mean numeric features\n    cat_indices = [X_te.columns.get_loc(c) for c in cat_cols if c in X_te.columns]\n\n    skf = StratifiedKFold(n_splits=N_FOLDS, shuffle=True, random_state=SEED)\n    oof = np.zeros(len(X_te))\n    models = []\n\n    for fold, (tr_idx, val_idx) in enumerate(skf.split(X_te, y), start=1):\n        print(f\"Fold {fold}/{N_FOLDS}\")\n\n        cb = CatBoostClassifier(**CAT_PARAMS)\n        cb.fit(\n            Pool(X_te.iloc[tr_idx], label=y.iloc[tr_idx], cat_features=cat_indices if cat_indices else None),\n            eval_set=Pool(X_te.iloc[val_idx], label=y.iloc[val_idx], cat_features=cat_indices if cat_indices else None),\n            use_best_model=True,\n            verbose=False,\n        )\n        preds = cb.predict_proba(Pool(X_te.iloc[val_idx], cat_features=cat_indices if cat_indices else None))[:,1]\n        oof[val_idx] = preds\n        models.append(cb)\n        print(f\"  CatBoost AUC: {roc_auc_score(y.iloc[val_idx], preds):.5f}\")\n\n    auc_oof = roc_auc_score(y, oof)\n    print(f\"\\nOOF CatBoost AUC: {auc_oof:.5f}\")\n\n    # Calibrate OOF\n    iso = IsotonicRegression(out_of_bounds=\"clip\").fit(oof, y.values)\n\n    # Full fit on all data\n    cb_full = CatBoostClassifier(**CAT_PARAMS)\n    cb_full.fit(Pool(X_te, label=y, cat_features=cat_indices if cat_indices else None), verbose=False)\n\n    # Test predictions\n    test_cb = cb_full.predict_proba(Pool(T_te, cat_features=cat_indices if cat_indices else None))[:,1]\n    test_cb_cal = np.clip(iso.predict(test_cb), 0.0, 1.0)\n\n    pd.DataFrame({\"id\": test_ids, \"loan_paid_back\": test_cb_cal}).to_csv(\"submission.csv\", index=False)\n    print(\"\\nSaved submission.csv\")\n\nif __name__ == \"__main__\":\n    run()","metadata":{"_uuid":"8f2839f25d086af736a60e9eeb907d3b93b6e0e5","_cell_guid":"b1076dfc-b9ad-4769-8c92-a6c4dae69d19","trusted":true},"outputs":[],"execution_count":null}]}